s = filesep();
path = jimlabPath + 'tests' + s + 'images' + s + 'noError' + s + 'rgba.png';
jimage = jimread(path);

jgray = jimconvert(jimage, 'gray');
gray = jimconvert(jimage.image, 'gray');
jrgb = jimconvert(jimage, 'rgb');
rgb = jimconvert(jimage.image, 'rgb');

jgray.encoding
jrgb.encoding

subplot(1,2,1)
jimdisp(rgb)
subplot(1,2,2)
jimdisp(gray)
