refsection.programlisting:
//Objet de type jimage(gray image)
path = jimlabPath("/") + "tests/images/logoEnsim_gray.png";
jimage1 = jimread(path);
blurred1 = jimsmooth(jimage1, "gaussian", 9);
subplot(2,2,1), jimdisp(jimage1)
subplot(2,2,2), jimdisp(blurred1)

//Objet de type jimage(RGB image)
 path1 = jimlabPath("/") + "tests/images/lena_color.gif";
jimage2 = jimread(path1);
blurred2 = jimsmooth(jimage2,"gaussian",9);
subplot(2,2,3), jimdisp(jimage2)
subplot(2,2,4), jimdisp(blurred2)

refsection.scilab:image:

	//Objet de type jimage(RGB  image)
	 path = jimlabPath("/") + "tests/images/logoEnsim_rgb.png";
    jimage1 = jimread(path);

	blurred1 = jimsmooth(jimage1,"gaussian",9);
	subplot(2,2,1), jimdisp(jimage1),title('original image')
	subplot(2,2,2), jimdisp(blurred1),title('gaussian filter')

	blurred2 = jimsmooth(jimage1,"uniform",9);
	blurred3 = jimsmooth(jimage1,"triangular",9);
	subplot(2,2,3), jimdisp(blurred2),title('uniform filter')
	subplot(2,2,4), jimdisp(blurred3),title('triangular filter')
