refsection.programlisting:

//Objet de type jimage(gray image)

jimage1 = jimread(jimlabPath("/") + 'tests/images/noError/gray.jpg');
smoothimage1 = jimsmooth(jimage1,"gaussian",9);
subplot(2,2,1)
jimdisp(jimage1)
subplot(2,2,2)
jimdisp(smoothimage1)

//Objet de type jimage(RGB image)

jimage2 = jimread(jimlabPath("/") + 'tests/images/noError/RGB.jpg');
smoothimage2 = jimsmooth(jimage2,"gaussian",9);
subplot(2,2,1)
jimdisp(jimage2)
subplot(2,2,2)
jimdisp(smoothimage2)
